// Extract social media links from website HTML
const html = $json.body || '';
const website = $node["Set Exchange Data"].json.website;

// Common social media patterns to look for
const socialPatterns = {
  twitter: /(?:twitter\.com\/|x\.com\/)([a-zA-Z0-9_]+)/gi,
  youtube: /(?:youtube\.com\/(?:c\/|channel\/|@)|youtu\.be\/)([a-zA-Z0-9_-]+)/gi,
  linkedin: /linkedin\.com\/(?:company\/|in\/)([a-zA-Z0-9_-]+)/gi,
  facebook: /facebook\.com\/([a-zA-Z0-9_.-]+)/gi,
  instagram: /instagram\.com\/([a-zA-Z0-9_.-]+)/gi,
  telegram: /t\.me\/([a-zA-Z0-9_]+)/gi,
  discord: /discord\.gg\/([a-zA-Z0-9]+)/gi,
  reddit: /reddit\.com\/r\/([a-zA-Z0-9_]+)/gi,
  tiktok: /tiktok\.com\/@([a-zA-Z0-9_.-]+)/gi,
  medium: /medium\.com\/@([a-zA-Z0-9_.-]+)/gi,
  github: /github\.com\/([a-zA-Z0-9_.-]+)/gi
};

// App store patterns
const appPatterns = {
  apple: /apps\.apple\.com\/app\/[^"'\s]+/gi,
  android: /play\.google\.com\/store\/apps\/details\?id=[^"'\s]+/gi
};

// Wikipedia pattern
const wikipediaPattern = /en\.wikipedia\.org\/wiki\/[^"'\s]+/gi;

const socialLinks = { website };

// Extract social media links
Object.entries(socialPatterns).forEach(([platform, pattern]) => {
  const matches = [...html.matchAll(pattern)];
  if (matches.length > 0) {
    const match = matches[0];
    let url = match[0];

    // Clean up the URL
    if (url.startsWith('//')) url = 'https:' + url;
    if (!url.startsWith('http')) url = 'https://' + url;

    // Remove any trailing characters that aren't part of the URL
    url = url.replace(/[\s"'<>]+$/, '');

    socialLinks[platform] = url;
  }
});

// Extract app store links
Object.entries(appPatterns).forEach(([platform, pattern]) => {
  const matches = [...html.matchAll(pattern)];
  if (matches.length > 0) {
    let url = matches[0][0];
    if (!url.startsWith('http')) url = 'https://' + url;
    url = url.replace(/[\s"'<>]+$/, '');
    socialLinks[platform] = url;
  }
});

// Extract Wikipedia link
const wikiMatches = [...html.matchAll(wikipediaPattern)];
if (wikiMatches.length > 0) {
  let url = wikiMatches[0][0];
  if (!url.startsWith('http')) url = 'https://' + url;
  url = url.replace(/[\s"'<>]+$/, '');
  socialLinks.wikipedia = url;
}

// Clean up any empty or invalid URLs
Object.keys(socialLinks).forEach(key => {
  if (!socialLinks[key] || socialLinks[key] === website || socialLinks[key].length < 10) {
    delete socialLinks[key];
  }
});

console.log('Scraped social links:', Object.keys(socialLinks));

return [{
  json: {
    socialLinks,
    scrapedCount: Object.keys(socialLinks).length
  }
}];

function decodeFile(nodeName, parseJson=false) {
  const node = $node[nodeName].json;
  if (!node.content) throw new Error(`${nodeName} has no json.content`);
  const buff = Buffer.from(node.content, 'base64');
  const text = buff.toString('utf-8');
  return parseJson ? JSON.parse(text) : text;
}

const db = decodeFile("Get content-database.json", true);
const guidelinesText = decodeFile("Get Guidelines", false);
const { name, website } = $node["Set Exchange Data"].json;

// Get scraped social links (with fallback to empty object)
let scrapedSocialLinks = {};
try {
  const socialData = $node["Extract Social Links"].json;
  scrapedSocialLinks = socialData.socialLinks || {};
  console.log('Using scraped social links:', Object.keys(scrapedSocialLinks));
} catch (e) {
  console.log('No scraped social links available, will use AI generation as fallback');
}

// Convert database to compact format: key|slug|title
// This reduces token usage by ~80% while preserving all necessary data
function formatDbCompact(dbObj, category) {
  return Object.entries(dbObj || {})
    .map(([key, val]) => {
      // Prepend category prefix to slug (e.g., /crypto-ogs/, /posts/, /exchanges/, /tools/)
      const fullSlug = `/${category}/${val.slug}`;
      return [key, fullSlug, val.title].join('|');
    })
    .join('\n');
}

const compactOgs = formatDbCompact(db.crypto_ogs, 'crypto-ogs');
const compactPosts = formatDbCompact(db.posts, 'posts');
const compactExchanges = formatDbCompact(db.exchanges, 'exchanges');
const compactTools = formatDbCompact(db.tools, 'tools');

return [{
  json: { 
    db,              // Full database for backend processing
    guidelinesText,
    name, 
    website,
    scrapedSocialLinks, // Scraped social links from website
    compactOgs,      // Compact format for AI (80% fewer tokens)
    compactPosts,
    compactExchanges,
    compactTools
  }
}];

