const meta = $node["Parse & Prepare Data"].json;
const ai = $node["OpenAI: Generate Body"].json;

const slug = meta.name.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/(^-|-$)/g, '');
const isoDate = new Date().toISOString().split('T')[0]; // YYYY-MM-DD format
const order = meta.db.next_orders.exchanges;

// Use scraped social links as primary source, with AI fallback
let socialLinks = { website: meta.website };
let description = '';
let jsonFound = false;

// First, try to use scraped social links
if (meta.scrapedSocialLinks && Object.keys(meta.scrapedSocialLinks).length > 0) {
  socialLinks = { ...socialLinks, ...meta.scrapedSocialLinks };
  console.log('Using scraped social links:', Object.keys(meta.scrapedSocialLinks));
  jsonFound = true;
}

try {
  const aiContent = ai.choices?.[0]?.message?.content || ai.content || '';
  console.log('AI Response length:', aiContent.length);

  // Look for JSON object in the response
  const jsonMatch = aiContent.match(/\{[\s\S]*?"social"[\s\S]*?\}/g);
  if (jsonMatch && jsonMatch.length > 0) {
    console.log('Found JSON match:', jsonMatch[0].substring(0, 200));
    const aiData = JSON.parse(jsonMatch[0]);

    // Only use AI social links if scraping failed
    if (!jsonFound && aiData.social) {
      socialLinks = { ...socialLinks, ...aiData.social };
      console.log('Using AI-generated social links as fallback:', Object.keys(aiData.social));
      jsonFound = true;
    }

    // Extract description (always needed)
    if (aiData.description) {
      description = aiData.description;
      console.log('Extracted description:', description);
    }
  }

  // Validate we got the required data
  if (!jsonFound) {
    throw new Error('No social links found from scraping or AI. Manual review needed.');
  }

  if (!description) {
    throw new Error('AI did not return required description. Manual review needed.');
  }

} catch (e) {
  console.log('ERROR parsing AI response:', e.message);
  throw new Error(`Failed to extract data from AI response: ${e.message}. Please check AI output format.`);
}

// Build social media section for frontmatter
const socialSection = Object.entries(socialLinks)
  .filter(([key, value]) => value && value.trim())
  .map(([key, value]) => `  ${key}: ${value}`)
  .join('\n');

const frontmatter = [
  '---',
  `title: "${meta.name}"`,
  `image: "/images/exchanges/${slug}.png"`,
  `description: "${description}"`,
  `date: ${isoDate}`,
  `order: ${order}`,
  `authors: ["Crypto Wiki Team"]`,
  `social:`,
  socialSection,
  '---'
].join('\n');

// Extract just the markdown body (remove any JSON at the end)
let body = ai.choices?.[0]?.message?.content || ai.content || '';
// Remove JSON object if it exists at the end
body = body.replace(/\n\{[\s\S]*"social"[\s\S]*\}$/, '').trim();

return [{
  json: {
    mdx: `${frontmatter}\n\n${body}`,
    slug,
    name: meta.name,
    website: meta.website,
    isoDate,
    order,
    db: meta.db,
    socialLinks
  }
}];

const data = $json;
const filePath = `${data.slug}.mdx`;
const commitMessage = `New exchange: ${data.name}`;

return [{
  json: {
    ...data,
    filePath,
    commitMessage
  }
}];

// Validate file write was successful
const fileResult = $input.first().json;
if (!fileResult || fileResult.error) {
  throw new Error(`File write failed: ${fileResult?.error || 'Unknown error'}`);
}

// Get exchange data from Build Frontmatter + MDX node
const exchangeData = $node["Build Frontmatter + MDX"].json;
if (!exchangeData || !exchangeData.name || !exchangeData.slug) {
  throw new Error('Exchange data not found or incomplete');
}

// Get and validate database data
const dbData = exchangeData.db;
if (!dbData) {
  throw new Error('Database data not found');
}

// Create updated database with proper structure
const updatedDb = {
  ...dbData,
  exchanges: { ...(dbData.exchanges || {}) },
  next_orders: { ...(dbData.next_orders || { exchanges: 1 }) }
};

// Create exchange key (lowercase, replace spaces with dots)
const exchangeKey = exchangeData.name.toLowerCase().replace(/\s+/g, '.');

// Check if exchange already exists
if (updatedDb.exchanges[exchangeKey]) {
  throw new Error(`Exchange '${exchangeData.name}' already exists in database`);
}

// Add new exchange to database (slug without /exchanges/ prefix)
updatedDb.exchanges[exchangeKey] = {
  slug: exchangeData.slug,
  title: exchangeData.name
};

// Increment next order counter
updatedDb.next_orders.exchanges = (updatedDb.next_orders.exchanges || 0) + 1;

// Return success with updated database
return [{ 
  json: { 
    db: updatedDb, 
    fileSuccess: true, 
    dbSuccess: true,
    exchangeAdded: exchangeKey,
    name: exchangeData.name
  } 
}];
