function decodeFile(nodeName, parseJson=false) {
  const node = $node[nodeName].json;
  if (!node.content) throw new Error(`${nodeName} has no json.content`);
  const buff = Buffer.from(node.content, 'base64');
  const text = buff.toString('utf-8');
  return parseJson ? JSON.parse(text) : text;
}

const db = decodeFile("Get content-database.json", true);
const guidelinesText = decodeFile("Get Guidelines", false);
const { name, website } = $node["Set Exchange Data"].json;

// Convert database to compact format: key|slug|title
// This reduces token usage by ~80% while preserving all necessary data
function formatDbCompact(dbObj, category) {
  return Object.entries(dbObj || {})
    .map(([key, val]) => {
      // Prepend category prefix to slug (e.g., /crypto-ogs/, /posts/, /exchanges/, /tools/)
      const fullSlug = `/${category}/${val.slug}`;
      return [key, fullSlug, val.title].join('|');
    })
    .join('\n');
}

const compactOgs = formatDbCompact(db.crypto_ogs, 'crypto-ogs');
const compactPosts = formatDbCompact(db.posts, 'posts');
const compactExchanges = formatDbCompact(db.exchanges, 'exchanges');
const compactTools = formatDbCompact(db.tools, 'tools');

return [{
  json: { 
    db,              // Full database for backend processing
    guidelinesText,
    name, 
    website,
    compactOgs,      // Compact format for AI (80% fewer tokens)
    compactPosts,
    compactExchanges,
    compactTools
  }
}];

const meta = $node["Parse & Prepare Data"].json;
const ai = $node["OpenAI: Generate Body"].json;

const slug = meta.name.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/(^-|-$)/g, '');
const isoDate = new Date().toISOString().split('T')[0]; // YYYY-MM-DD format
const order = meta.db.next_orders.exchanges;

// Extract social media links and description from AI response
let socialLinks = { website: meta.website };
let description = '';
let jsonFound = false;

// Function to validate social media URLs
function isValidSocialUrl(url, platform) {
  if (!url || typeof url !== 'string') return false;

  // Basic URL validation
  try {
    new URL(url);
  } catch {
    return false;
  }

  // Platform-specific validation patterns
  const patterns = {
    twitter: /^https:\/\/(twitter\.com|x\.com)\//,
    linkedin: /^https:\/\/www\.linkedin\.com\/company\//,
    facebook: /^https:\/\/(www\.)?facebook\.com\//,
    instagram: /^https:\/\/(www\.)?instagram\.com\//,
    youtube: /^https:\/\/(www\.)?youtube\.com\//,
    telegram: /^https:\/\/t\.me\//,
    discord: /^https:\/\/discord\.(com|gg)\//,
    reddit: /^https:\/\/(www\.)?reddit\.com\//,
    tiktok: /^https:\/\/(www\.)?tiktok\.com\//,
    apple: /^https:\/\/apps\.apple\.com\//,
    android: /^https:\/\/play\.google\.com\//,
    wikipedia: /^https:\/\/(en\.)?wikipedia\.org\//
  };

  return patterns[platform] ? patterns[platform].test(url) : true;
}

// Function to filter out generic/guessed URLs
function filterGenericUrls(socialObj) {
  const filtered = {};

  for (const [platform, url] of Object.entries(socialObj)) {
    if (platform === 'website') {
      filtered[platform] = url; // Always keep website
      continue;
    }

    // Skip if URL looks generic/guessed
    const genericPatterns = [
      new RegExp(`https://${platform}\.com/${meta.name.toLowerCase()}`),
      new RegExp(`https://${platform}\.com/company/${meta.name.toLowerCase()}`),
      new RegExp(`https://${platform}\.com/@${meta.name.toLowerCase()}`)
    ];

    const isGeneric = genericPatterns.some(pattern => pattern.test(url));

    if (!isGeneric && isValidSocialUrl(url, platform)) {
      filtered[platform] = url;
    } else {
      console.log(`Filtered out generic/invalid ${platform} URL: ${url}`);
    }
  }

  return filtered;
}

try {
  const aiContent = ai.choices?.[0]?.message?.content || ai.content || '';
  console.log('AI Response length:', aiContent.length);

  // Look for JSON object in the response
  const jsonMatch = aiContent.match(/\{[\s\S]*?"social"[\s\S]*?\}/g);
  if (jsonMatch && jsonMatch.length > 0) {
    console.log('Found JSON match:', jsonMatch[0].substring(0, 200));

    // Clean up the JSON string before parsing
    let jsonString = jsonMatch[0];

    // Fix common JSON issues
    jsonString = jsonString
      .replace(/,(s*[}\]])/g, '$1') // Remove trailing commas
      .replace(/([^,{}\s])(s*[}\]])/g, '$1$2') // Ensure proper comma placement
      .replace(/([^,{}\s])(s*"[a-zA-Z_][a-zA-Z0-9_]*"s*:)/g, '$1,$2'); // Add missing commas before properties

    try {
      const aiData = JSON.parse(jsonString);

      // Extract social links
      if (aiData.social) {
        socialLinks = { ...socialLinks, ...aiData.social };
        console.log('Extracted social links:', Object.keys(aiData.social));

        // Filter out generic/guessed URLs
        socialLinks = filterGenericUrls(socialLinks);
        console.log('Filtered social links:', Object.keys(socialLinks));

        jsonFound = true;
      }

      // Extract description
      if (aiData.description) {
        description = aiData.description;
        console.log('Extracted description:', description);
      }
    } catch (jsonError) {
      console.log('JSON parsing failed, trying manual extraction:', jsonError.message);

      // Manual extraction as fallback - search in the full content
      const socialMatch = aiContent.match(/"social"\s*:\s*\{([^}]+)\}/);
      const descMatch = aiContent.match(/"description"\s*:\s*"([^"]+)"/);

      if (socialMatch) {
        // Extract individual social links manually
        const socialText = socialMatch[1];
        const linkMatches = socialText.matchAll(/"([^"]+)"\s*:\s*"([^"]+)"/g);

        for (const match of linkMatches) {
          socialLinks[match[1]] = match[2];
        }

        // Filter out generic/guessed URLs
        socialLinks = filterGenericUrls(socialLinks);

        jsonFound = true;
        console.log('Manual extraction successful:', Object.keys(socialLinks));
      }

      if (descMatch) {
        description = descMatch[1];
        console.log('Manual description extraction:', description);
      }
    }
  }

  // Validate we got the required data
  if (!jsonFound) {
    throw new Error('AI did not return required JSON with social links. Manual review needed.');
  }

  if (!description) {
    throw new Error('AI did not return required description. Manual review needed.');
  }

  // Check if we have minimum required social links
  const socialCount = Object.keys(socialLinks).length;
  if (socialCount < 3) {
    console.log(`Warning: Only ${socialCount} social links found. Consider manual review.`);
  }

} catch (e) {
  console.log('ERROR parsing AI response:', e.message);
  throw new Error(`Failed to extract data from AI response: ${e.message}. Please check AI output format.`);
}

// Build social media section for frontmatter
const socialSection = Object.entries(socialLinks)
  .filter(([key, value]) => value && value.trim())
  .map(([key, value]) => `  ${key}: ${value}`)
  .join('\n');

const frontmatter = [
  '---',
  `title: "${meta.name}"`,
  `image: "/images/exchanges/${slug}.png"`,
  `description: "${description}"`,
  `date: ${isoDate}`,
  `order: ${order}`,
  `authors: ["Crypto Wiki Team"]`,
  `social:`,
  socialSection,
  '---'
].join('\n');

// Extract just the markdown body (remove any JSON at the end)
let body = ai.choices?.[0]?.message?.content || ai.content || '';
// Remove JSON object if it exists at the end
body = body.replace(/\n\{[\s\S]*"social"[\s\S]*\}$/, '').trim();

return [{
  json: {
    mdx: `${frontmatter}\n\n${body}`,
    slug,
    name: meta.name,
    website: meta.website,
    isoDate,
    order,
    db: meta.db,
    socialLinks
  }
}];

const data = $json;
const filePath = `${data.slug}.mdx`;
const commitMessage = `New exchange: ${data.name}`;

return [{
  json: {
    ...data,
    filePath,
    commitMessage
  }
}];

// Validate file write was successful
const fileResult = $input.first().json;
if (!fileResult || fileResult.error) {
  throw new Error(`File write failed: ${fileResult?.error || 'Unknown error'}`);
}

// Get exchange data from Build Frontmatter + MDX node
const exchangeData = $node["Build Frontmatter + MDX"].json;
if (!exchangeData || !exchangeData.name || !exchangeData.slug) {
  throw new Error('Exchange data not found or incomplete');
}

// Get and validate database data
const dbData = exchangeData.db;
if (!dbData) {
  throw new Error('Database data not found');
}

// Create updated database with proper structure
const updatedDb = {
  ...dbData,
  exchanges: { ...(dbData.exchanges || {}) },
  next_orders: { ...(dbData.next_orders || { exchanges: 1 }) }
};

// Create exchange key (lowercase, replace spaces with dots)
const exchangeKey = exchangeData.name.toLowerCase().replace(/\s+/g, '.');

// Check if exchange already exists
if (updatedDb.exchanges[exchangeKey]) {
  throw new Error(`Exchange '${exchangeData.name}' already exists in database`);
}

// Add new exchange to database (slug without /exchanges/ prefix)
updatedDb.exchanges[exchangeKey] = {
  slug: exchangeData.slug,
  title: exchangeData.name
};

// Increment next order counter
updatedDb.next_orders.exchanges = (updatedDb.next_orders.exchanges || 0) + 1;

// Return success with updated database
return [{ 
  json: { 
    db: updatedDb, 
    fileSuccess: true, 
    dbSuccess: true,
    exchangeAdded: exchangeKey,
    name: exchangeData.name
  } 
}];
